SELECT tb_customer_coupon.coupon_id
FROM tb_customer_coupon,
tb_coupon
WHERE tb_customer_coupon.coupon_id = tb_coupon.coupon_id
AND tb_customer_coupon.customer_id = #{userId}
AND tb_coupon.category_id = #{categoryId}
AND tb_coupon.criterion <= #{price}
ORDER BY tb_coupon.bonus DESC
LIMIT 1

SELECT SUM(tb_item.price * tb_cart_customer.product_num)
FROM tb_cart_customer,tb_item,tb_cart
WHERE tb_cart_customer.product_id = tb_item.id
AND tb_cart_customer.cart_id = tb_cart.cart_id
AND tb_item.cid = #{categoryId}
AND tb_cart.customer_id = #{userId}

SELECT tb_customer_coupon.coupon_id
FROM tb_customer_coupon,
tb_coupon
WHERE tb_customer_coupon.coupon_id = tb_coupon.coupon_id
AND tb_customer_coupon.customer_id = #{userId}
AND tb_coupon.category_id = #{categoryId}
AND tb_coupon.criterion <= #{price}
